function stickyNavigation() {
    $("header").before($("header > .container").clone().removeClass("container").addClass("header -sticky").wrapInner(".container"))
}

function menuClose() {
    $(".-mobile-navigation").is(".-active") && ($(".-mobile-navigation").removeClass("-active"), $(".tcon-transform").removeClass("tcon-transform"))
}

function mobileNavigation() {
    $(".-mobile-navigation").toggleClass("-active")
}

function closeModal(e) {
    var a = $(e).closest(".modal"),
        o = a.find(".modal-content");
    a.removeClass("open"), o.empty()
}

function openModal(e) {
    var a = $(".modal");
    var el = $(e);
    var o = a.find(".modal-content");
    if(el.css("background-image") || el.children("img")){
        var p = null;
        if(el.css("background-image")){
            var p = el.css("background-image").replace('url(','').replace(')','').replace(/\"/gi, "");
        } else if(el.children("img"))  {
            var p = el.children("img").attr("src");
        }
        console.log(p);
        a.addClass("open");
        o.append("<div class='responsive-media' style='background:url(" + p + ") center center / cover no-repeat transparent'>");
    }
}

function tabs() {
    var e = $(".tabs"),
        a = e.find(".tabs-navigation a");
    a.each(function() {
        $(this).on("click", function() {
            var e = latinize($(this).text().replace(/ /g, "-").toLowerCase()),
                a = $(this).closest(".tabs").find(".tabs-content");
            "tudo" != e ? (a.children().not("." + e).hide(), a.children("." + e).show()) : a.children().show()
        })
    })
}
$( ".navigation.-menu a" ).each(function() {
    var e = $(this);
    if(e.attr("href")=="#"){
        e.attr({"href":"javascript:void(0)","onclick":"scrollNav(this)"})
    }
});
function scrollNav(a){
    var a = $(a);
    var b = $("body");
    var p = latinize(a.text().replace(/ /g, '-').toLowerCase());
    if(b.is(".pg-home")){
        var sp = $("#"+p).offset().top;
        console.log(sp);
        $("html, body").animate({scrollTop: sp-60}, 800); 
    } else {
        a.attr("href","http://localhost/jobs/Wordpress/brava-club/#"+p);
    }
}
